            const totalDiv = document.getElementById('total-price');

            if (currentOrder.length === 0) {
                orderDiv.innerHTML = '<p class="text-gray-400 text-center py-8">Tiada pesanan lagi</p>';
                totalDiv.textContent = 'RM 0.00';
