                total: total,
                timestamp: new Date().toLocaleString('ms-MY', { timeZone: 'Asia/Kuala_Lumpur' }),
                synced: false
            };

            salesHistory.push(sale);
            localStorage.setItem('salesHistory', JSON.stringify(salesHistory));

            currentOrder = [];
