class AgeSafetyLayer {
  verify(childAge, toyAgeRating) {
    const ageGroups = {
      '0-3': { choking: 'CRITICAL', supervision: 'CONSTANT' },
      '4-7': { choking: 'HIGH', supervision: 'FREQUENT' },
      '8-12': { choking: 'MODERATE', supervision: 'PERIODIC' },
      '13+': { choking: 'LOW', supervision: 'MINIMAL' }
    };

    const childGroup = this.determineAgeGroup(childAge);
    const toyGroup = toyAgeRating;

    return {
      safe: this.ageGroupCompatible(childGroup, toyGroup),
      childAge,
      toyRating: toyAgeRating,
      warnings: ageGroups[childGroup]
    };
  }

  ageGroupCompatible(childGroup, toyGroup) {
    const hierarchy = ['0-3', '4-7', '8-12', '13+'];
    return hierarchy.indexOf(childGroup) >= hierarchy.indexOf(toyGroup);
  }
}

