// Create: src/modules/vaultdns.js
export async function createDNSHook(subdomain, targetURL, env) {
  // Auto-create DNS record via Cloudflare API
  const response = await fetch(
    `https://api.cloudflare.com/client/v4/zones/${env.ZONE_ID}/dns_records`,
    {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${env.CLOUDFLARE_API_TOKEN}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        type: 'CNAME',
        name: subdomain,
        content: targetURL,
        proxied: true
      })
    }
  );

  return await response.json();
}

