export async function registerClaimRoot(brandData, deployment, env) {
  // Register in LicenseVault with ClaimRoot anchoring
  const claimRoot = generateClaimRoot(brandData);

  const response = await fetch(
    `${env.LICENSEVAULT_URL}/api/claimroot/register`,
    {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        claimRoot,
        brandName: brandData.businessName,
        owner: brandData.owner,
        deploymentUrl: deployment.url,
        timestamp: new Date().toISOString()
      })
