  return await response.json();
}

function generateClaimRoot(brandData) {
  const hash = crypto.createHash('sha256')
    .update(`${brandData.businessName}-${Date.now()}`)
    .digest('hex');
  return `CLAIM_${hash.substring(0, 16).toUpperCase()}`;
}

