async function handleHotStackUpload(request, env, corsHeaders, ctx) {
  const startTime = Date.now();
  const buildId = crypto.randomUUID();

  try {
    const formData = await request.formData();
    const file = formData.get('file');
    const prompt = formData.get('prompt');

    // 1. Extract content
    const content = await extractFileContent(file);

    // 2. AI analysis
    const brandIntent = env.ANTHROPIC_API_KEY 
      ? await processWithClaude(content, prompt, env)
      : generateMockBrandIntent(prompt, file.name);

    // 3. LicenseVault verification
    const verification = await verifyBrandWithLicenseVault(brandIntent.businessName);
    if (!verification.available) {
      return errorResponse('Brand name taken', 409, corsHeaders);
    }

    // 4. Get template pack ← NEW
    const template = await getCuratedTemplates(brandIntent.businessType, env);

    // 5. Apply template ← NEW
    const brandSite = await applyTemplate(brandIntent, template);

    // 6. Create DNS hook ← NEW
    const subdomain = slugify(brandIntent.businessName);
    const dns = await createDNSHook(
      subdomain,
      'hotstack-bucket.r2.cloudflarestorage.com',
      env
    );

    // 7. Deploy to R2
    await deployBrandSite(subdomain, brandSite, env);

    // 8. Register ClaimRoot ← NEW
    const claimRoot = await registerClaimRoot(brandIntent, { url: dns.url }, env);

    // 9. Launch CodeNest dashboard ← NEW
    const dashboard = await launchCodeNest({
      id: buildId,
      brandName: brandIntent.businessName,
      url: `https://${subdomain}.faa.zone`
    }, env);

    // 10. Store build record
    const buildRecord = {
      buildId,
      subdomain,
      brandName: brandIntent.businessName,
      claimRoot: claimRoot.id,
      dashboardUrl: dashboard.managementUrl,
      deployedUrl: `https://${subdomain}.faa.zone`,
      elapsedTime: Math.round((Date.now() - startTime) / 1000),
      status: 'completed'
    };

    await env.BUILDNEST_KV.put(
      `build:${buildId}`,
      JSON.stringify(buildRecord)
    );

    return new Response(JSON.stringify({
      success: true,
      buildId,
      deployment: {
        url: buildRecord.deployedUrl,
        email: `hello@${subdomain}.faa.zone`,
        dashboard: dashboard.managementUrl
      },
      claimRoot: claimRoot.id,
      elapsedTime: buildRecord.elapsedTime
    }), {
      headers: { 'Content-Type': 'application/json', ...corsHeaders }
    });

  } catch (error) {
    return errorResponse(error.message, 500, corsHeaders);
  }
}

