// Add this inside the AntsVaultMeshLevel7System class, after setupDeploymentListener():

setupiCloudSyncListener() {
    const icloudBtn = document.getElementById('banimal-icloud-sync-btn');
    if (icloudBtn) {
        icloudBtn.addEventListener('click', () => {
            this.triggeriCloudSync(icloudBtn);
        });
    }
}

triggeriCloudSync(buttonElement) {
    this.log('🍎 iCloud Sync: Initiating file pull...');
    const originalText = buttonElement.textContent;
    buttonElement.textContent = '⏳ Syncing iCloud...';
    buttonElement.disabled = true;

    const formData = new FormData();
    formData.append('action', 'banimal_icloud_sync');
    formData.append('security', this.ajaxData.security_nonce);

    fetch(this.ajaxData.ajax_url, { method: 'POST', body: formData })
    .then(response => response.json())
    .then(data => {
        if (data.success) {
            this.log('🟢 iCloud SYNC SUCCESS: ' + data.data.files_synced + ' files');
            buttonElement.textContent = '✅ Sync Complete!';
            buttonElement.style.background = '#10b981';
        } else {
            this.log('🔴 iCloud SYNC FAILED: ' + (data.data.message || 'Unknown error'));
            buttonElement.textContent = '❌ Sync Failed';
            buttonElement.style.background = '#ef4444';

            if (data.data.instructions) {
                console.log('📋 Setup Instructions:', data.data.instructions);
            }
        }
    })
    .catch(error => {
        this.log('🚨 iCloud Sync Error: ' + error.message);
        buttonElement.textContent = '⚠️ Error';
        buttonElement.style.background = '#f59e0b';
    })
    .finally(() => {
        setTimeout(() => {
            buttonElement.textContent = originalText;
            buttonElement.disabled = false;
            buttonElement.style.background = '';
        }, 5000);
    });
}

