setupGoogleDriveListeners() {
    // Connect buttons
    const authButtons = document.querySelectorAll('.gdrive-auth-btn');
    authButtons.forEach(btn => {
        btn.addEventListener('click', () => {
            const account = btn.getAttribute('data-account');
            this.connectGoogleDrive(account, btn);
        });
    });

    // Sync buttons
    const syncButtons = document.querySelectorAll('.gdrive-sync-btn');
    syncButtons.forEach(btn => {
        btn.addEventListener('click', () => {
            const account = btn.getAttribute('data-account');
            this.syncGoogleDrive(account, btn);
        });
    });

    // Sync all button
    const syncAllBtn = document.getElementById('banimal-sync-all-btn');
    if (syncAllBtn) {
        syncAllBtn.addEventListener('click', () => {
            this.syncAllDrives(syncAllBtn);
        });
    }
}

