syncGoogleDrive(account, buttonElement) {
    this.log(`🔄 Syncing Google Drive account: ${account}`);
    const originalText = buttonElement.textContent;
    buttonElement.textContent = '⏳ Syncing...';
    buttonElement.disabled = true;

    const formData = new FormData();
    formData.append('action', 'banimal_gdrive_sync');
    formData.append('account', account);
    formData.append('security', this.ajaxData.security_nonce);

    fetch(this.ajaxData.ajax_url, { method: 'POST', body: formData })
    .then(response => response.json())
    .then(data => {
        if (data.success) {
            this.log(`✅ Sync complete: ${data.data.file_count} files from ${account}`);
            buttonElement.textContent = '✅ Synced!';
            buttonElement.style.background = '#10b981';
        } else {
            this.log(`❌ Sync failed: ${data.data || 'Unknown error'}`);
            buttonElement.textContent = '❌ Failed';
            buttonElement.style.background = '#ef4444';
        }
    })
    .catch(error => {
        this.log(`🚨 Sync error: ${error.message}`);
        buttonElement.textContent = '⚠️ Error';
        buttonElement.style.background = '#f59e0b';
    })
    .finally(() => {
        setTimeout(() => {
            buttonElement.textContent = originalText;
            buttonElement.disabled = false;
            buttonElement.style.background = '';
        }, 3000);
    });
}

