    }

    // Check if iCloud is authenticated
    $icloud_token = get_option('banimal_icloud_token', '');
    if (empty($icloud_token)) {
        wp_send_json_error([
            'message' => 'iCloud not authenticated',
            'instructions' => $this->icloud_connector->authenticate_icloud()
        ]);
    }

    // Perform sync
    $sync_result = $this->icloud_connector->sync_to_vault($this->vault_allocator);

    if ($sync_result === false) {
        wp_send_json_error(['message' => 'iCloud sync failed']);
    }

    wp_send_json_success([
        'message' => 'iCloud sync complete',
        'vault_id' => $sync_result['vault_id'],
        'files_synced' => $sync_result['files_synced']
    ]);
}

