public function handle_gdrive_sync() {
    // Security checks
    if ( ! current_user_can( 'manage_options' ) ) { 
        wp_send_json_error( 'Permission denied.' ); 
    }

    if ( ! isset($_POST['security']) || ! wp_verify_nonce( $_POST['security'], 'banimal-sync-nonce' ) ) {
        wp_send_json_error( 'Security verification failed.' );
    }

    // Get account to sync
    $account = isset($_POST['account']) ? sanitize_text_field($_POST['account']) : 'primary';

    // Fetch files from specific account
    $files = $this->gdrive_connector->fetch_google_drive_files($account);

    if (isset($files['status']) && $files['status'] === 'auth_required') {
        wp_send_json_error([
            'message' => 'Account not authenticated',
            'email' => $files['email']
        ]);
    }

    wp_send_json_success([
        'message' => 'Google Drive sync complete',
        'account' => $account,
        'email' => $files['email'],
        'file_count' => $files['file_count']
    ]);
}

