function banimal_publish_page_from_claude($request) {
    $params = $request->get_json_params();

    $page_data = array(
        'post_title'    => $params['title'],
        'post_content'  => $params['html'],
        'post_status'   => 'publish',
        'post_type'     => 'page',
        'post_author'   => 1
    );

    $page_id = wp_insert_post($page_data);

    return array(
        'success' => true,
        'page_id' => $page_id,
        'url' => get_permalink($page_id)
    );
}

function banimal_verify_api_key($request) {
    $api_key = $request->get_header('X-Banimal-Key');
    $stored_key = get_option('banimal_xai_api_key');
    return ($api_key === $stored_key);
}
