        #vaultmesh ul li {
            font-size: 1.1rem;
            margin-bottom: 0.75rem;
            position: relative;
            padding-left: 1.5rem;
        }
        #vaultmesh ul li::before { /* Custom bullet point */
            content: 'â€¢';
            position: absolute;
            left: 0;
            color: var(--primary-color); /* Electric blue bullet */
            font-size: 1.2em;
            line-height: 1;
            top: 0.1em;
