                responseDiv.textContent = aiResponse;

            } catch (error) {
                console.error("Error calling Gemini AIâ„¢ API:", error);
                responseDiv.textContent = ''; // Clear "Generating response..."
                errorDiv.textContent = \`Failed to get AIâ„¢ response: ${error.message}. Check your Gemini AIâ„¢ API key and network.\`;
                errorDiv.classList.remove('hidden');
