        {/* Preview Modal */}
        <Dialog open={isPreviewOpen} onOpenChange={setIsPreviewOpen}>
          <DialogContent className="max-w-7xl max-h-[90vh] overflow-hidden p-0">
            <DialogHeader className="p-6 pb-4">
              <DialogTitle className="flex items-center gap-3">
                <span className="text-2xl">{previewTemplate ? getTemplateTypeInfo(previewTemplate.type).icon : "ğŸ“„"}</span>
                <div>
                  <div className="text-xl font-bold">{previewTemplate?.name}</div>
                  <div className="text-sm text-muted-foreground font-normal">
                    {previewTemplate?.description} â€¢ Version {previewTemplate?.version}
                  </div>
                </div>
              </DialogTitle>
            </DialogHeader>

            <div className="flex-1 overflow-hidden">
              {previewTemplate && (
                <div className="h-[70vh] border rounded-lg mx-6 mb-6 overflow-hidden">
                  <iframe
                    srcDoc={previewTemplate.content}
                    className="w-full h-full border-0"
                    title={`Preview: ${previewTemplate.name}`}
                    sandbox="allow-scripts allow-same-origin"
                  />
