async function extractFileContent(file) {
  const arrayBuffer = await file.arrayBuffer();
  return new TextDecoder().decode(arrayBuffer);
}

async function processWithAILogic(content, prompt, env) {
  const response = await fetch('https://api.anthropic.com/v1/messages', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'x-api-key': env.ANTHROPIC_API_KEY,
      'anthropic-version': '2023-06-01'
    },
    body: JSON.stringify({
      model: 'claude-sonnet-4-20250514',
      max_tokens: 2000,
      messages: [{
        role: 'user',
        content: `Extract business intent as JSON:
CONTENT: ${content.substring(0, 5000)}
PROMPT: ${prompt}

Return JSON only:
{
  "businessName": "name",
  "businessType": "type",
  "sector": "North America|South America|Europe|Africa|Asia|Oceania",
  "location": "city, state",
  "services": ["service1", "service2"],
  "brandColors": {"primary": "#hex", "secondary": "#hex"},
  "treatyGlyphs": ["âš¡", "ðŸŒŠ"],
  "scrollClaimsRate": 0.10,
  "description": "description"
}`
      }]
    })
  });

  const result = await response.json();
  const jsonText = result.content[0].text.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
