    50% { 
        transform: scale(1.3);
        opacity: 0.7;
    }
}

.pulse-text {
    color: var(--fruitful-green);
    font-weight: bold;
    font-size: 0.875rem;
    letter-spacing: 1px;
}

/* ========================================
   HEADER
   ======================================== */

.main-header {
    background: rgba(15, 15, 35, 0.95);
    backdrop-filter: blur(10px);
    position: sticky;
    top: 0;
    z-index: 1000;
    border-bottom: 2px solid var(--fruitful-gold);
}

.header-content {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: var(--spacing-sm) 0;
}

.logo-section {
    display: flex;
    align-items: center;
    gap: 12px;
}

.bear-icon {
    font-size: 2.5rem;
    animation: bearBounce 3s ease-in-out infinite;
}

@keyframes bearBounce {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-5px); }
}

.site-title {
    font-size: 1.5rem;
    font-weight: 700;
    display: flex;
    align-items: center;
    gap: 8px;
}

.brand-fruitful {
    color: var(--fruitful-gold);
    text-shadow: var(--glow-gold);
}

.brand-separator {
    color: var(--text-secondary);
}

.brand-kidstv {
    color: var(--fruitful-green);
}

.main-nav {
    display: flex;
    gap: var(--spacing-md);
}

.nav-link {
    color: var(--text-secondary);
    text-decoration: none;
    font-weight: 500;
    transition: all 0.3s ease;
    padding: 8px 16px;
    border-radius: 8px;
}

.nav-link:hover,
.nav-link.active {
    color: var(--fruitful-gold);
    background: rgba(255, 215, 0, 0.1);
}

.location-badge {
    display: flex;
    align-items: center;
    gap: 8px;
    background: var(--sa-green);
    padding: 8px 16px;
    border-radius: 20px;
    font-size: 0.875rem;
    font-weight: 600;
}

/* ========================================
   HERO SECTION
   ======================================== */

.hero-section {
    padding: var(--spacing-lg) 0;
    background: linear-gradient(135deg, var(--bg-secondary) 0%, var(--bg-card) 100%);
    position: relative;
    overflow: hidden;
}

.hero-section::before {
    content: '🌍✨🧸📺';
    position: absolute;
    font-size: 8rem;
    opacity: 0.03;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    white-space: nowrap;
}

.hero-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-lg);
    align-items: center;
}

.hero-title {
    font-size: 3rem;
    margin-bottom: var(--spacing-sm);
    line-height: 1.2;
}

.ubuntu-word {
    color: var(--fruitful-gold);
    font-style: italic;
}

.hero-subtitle {
    font-size: 1.25rem;
    color: var(--text-secondary);
    margin-bottom: var(--spacing-md);
}

.hero-badges {
    display: flex;
    gap: var(--spacing-sm);
    margin-bottom: var(--spacing-md);
    flex-wrap: wrap;
}

.badge {
    padding: 8px 16px;
    border-radius: 20px;
    font-size: 0.875rem;
    font-weight: 600;
    display: flex;
    align-items: center;
    gap: 6px;
}

.badge-safe {
    background: var(--fruitful-green);
    color: white;
}

.badge-educational {
    background: var(--fruitful-blue);
    color: white;
}

.badge-african {
    background: var(--sa-gold);
    color: var(--bg-primary);
}

.cta-buttons {
    display: flex;
    gap: var(--spacing-sm);
}

.btn {
    padding: 14px 28px;
    border: none;
    border-radius: 8px;
    font-size: 1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    display: inline-flex;
    align-items: center;
    gap: 8px;
}

.btn-primary {
    background: var(--fruitful-gold);
    color: var(--bg-primary);
    box-shadow: var(--glow-gold);
}

.btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 20px rgba(255, 215, 0, 0.5);
}

.btn-secondary {
    background: transparent;
    color: var(--fruitful-gold);
    border: 2px solid var(--fruitful-gold);
}

.btn-secondary:hover {
    background: var(--fruitful-gold);
    color: var(--bg-primary);
}

.video-showcase {
    position: relative;
}

.video-player {
    border-radius: 16px;
    overflow: hidden;
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.5);
    position: relative;
    cursor: pointer;
    transition: transform 0.3s ease;
}

.video-player:hover {
    transform: scale(1.02);
}

.video-player img {
    width: 100%;
    display: block;
}

.play-overlay {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 80px;
    height: 80px;
    background: var(--fruitful-gold);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 2rem;
    color: var(--bg-primary);
    box-shadow: 0 8px 20px rgba(255, 215, 0, 0.5);
}

/* ========================================
   SHOWS SECTION
   ======================================== */

.shows-section {
    padding: var(--spacing-lg) 0;
}

.section-header {
    text-align: center;
    margin-bottom: var(--spacing-lg);
}

.section-title {
    font-size: 2.5rem;
    margin-bottom: var(--spacing-sm);
    color: var(--fruitful-gold);
}

.section-subtitle {
    font-size: 1.125rem;
    color: var(--text-secondary);
}

.age-filters {
    display: flex;
    justify-content: center;
    gap: var(--spacing-sm);
    margin-top: var(--spacing-md);
    flex-wrap: wrap;
}

.age-btn {
    padding: 10px 20px;
    background: var(--bg-card);
    color: var(--text-primary);
    border: 2px solid transparent;
    border-radius: 8px;
    cursor: pointer;
    font-weight: 600;
    transition: all 0.3s ease;
}

.age-btn:hover {
    border-color: var(--fruitful-gold);
}

.age-btn.active {
    background: var(--fruitful-gold);
    color: var(--bg-primary);
}

.shows-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
    gap: var(--spacing-md);
}

.show-card {
    background: var(--bg-card);
    border-radius: 12px;
    overflow: hidden;
    cursor: pointer;
    transition: all 0.3s ease;
}

.show-card:hover {
    transform: translateY(-8px);
    box-shadow: 0 12px 30px rgba(255, 215, 0, 0.3);
}

.show-thumbnail {
    width: 100%;
    height: 180px;
    object-fit: cover;
}

.show-info {
    padding: var(--spacing-sm);
}

.show-title {
    font-size: 1.125rem;
    margin-bottom: 0.5rem;
    color: var(--fruitful-gold);
}

.show-description {
    font-size: 0.875rem;
    color: var(--text-secondary);
    margin-bottom: var(--spacing-sm);
}

.show-meta {
    display: flex;
    justify-content: space-between;
    font-size: 0.75rem;
    color: var(--text-secondary);
}

/* ========================================
   LEARNING SECTION
   ======================================== */

.learning-section {
    padding: var(--spacing-lg) 0;
    background: var(--bg-secondary);
}

.learning-categories {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: var(--spacing-md);
}

.learning-card {
    background: var(--bg-card);
    padding: var(--spacing-md);
    border-radius: 12px;
    text-align: center;
    transition: all 0.3s ease;
    border: 2px solid transparent;
}

.learning-card:hover {
    transform: translateY(-5px);
    border-color: var(--fruitful-gold);
}

.learning-card.featured-card {
    background: linear-gradient(135deg, var(--sa-green), var(--sa-gold));
    color: white;
}

.card-icon {
    font-size: 4rem;
    margin-bottom: var(--spacing-sm);
}

.learning-card h3 {
    font-size: 1.5rem;
    margin-bottom: var(--spacing-xs);
    color: var(--fruitful-gold);
}

.featured-card h3 {
    color: white;
}

.learning-card p {
    color: var(--text-secondary);
    margin-bottom: var(--spacing-sm);
}

.featured-card p {
    color: rgba(255, 255, 255, 0.9);
}

.topic-list {
    list-style: none;
    text-align: left;
}

.topic-list li {
    padding: 6px 0;
    color: var(--text-secondary);
    display: flex;
    align-items: center;
    gap: 8px;
}

.featured-card .topic-list li {
    color: rgba(255, 255, 255, 0.9);
}

.topic-list i {
    color: var(--fruitful-green);
}

/* ========================================
   TOYS SECTION
   ======================================== */

.toys-section {
    padding: var(--spacing-lg) 0;
}

.toys-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-lg);
    align-items: center;
}

.bear-showcase {
    text-align: center;
    background: var(--bg-card);
    padding: var(--spacing-lg);
    border-radius: 20px;
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
}

.big-bear {
    font-size: 12rem;
    animation: bearFloat 6s ease-in-out infinite;
}

@keyframes bearFloat {
    0%, 100% { transform: translateY(0) rotate(0deg); }
    50% { transform: translateY(-20px) rotate(5deg); }
}

.features-list {
    margin: var(--spacing-md) 0;
}

.feature-item {
    display: flex;
    gap: var(--spacing-sm);
    margin-bottom: var(--spacing-md);
    padding: var(--spacing-sm);
    background: var(--bg-card);
    border-radius: 8px;
    transition: all 0.3s ease;
}

.feature-item:hover {
    background: var(--bg-secondary);
    transform: translateX(8px);
}

.feature-item i {
    font-size: 2rem;
    color: var(--fruitful-gold);
}

.feature-item h4 {
    margin-bottom: 4px;
    color: var(--fruitful-gold);
}

.feature-item p {
    color: var(--text-secondary);
    font-size: 0.875rem;
}

/* ========================================
   PARENT SECTION
   ======================================== */

.parent-section {
    padding: var(--spacing-lg) 0;
    background: var(--bg-secondary);
}

.dashboard-preview {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: var(--spacing-md);
}

.dashboard-card {
    background: var(--bg-card);
    padding: var(--spacing-md);
    border-radius: 12px;
    text-align: center;
    transition: all 0.3s ease;
    border: 2px solid transparent;
}

.dashboard-card:hover {
    border-color: var(--fruitful-blue);
    transform: translateY(-5px);
}

.dashboard-card i {
    font-size: 3rem;
    color: var(--fruitful-blue);
    margin-bottom: var(--spacing-sm);
}

.dashboard-card h3 {
    margin-bottom: var(--spacing-xs);
    color: var(--fruitful-gold);
}

.dashboard-card p {
    color: var(--text-secondary);
    font-size: 0.875rem;
}

/* ========================================
   FOOTER
   ======================================== */

.main-footer {
    background: var(--bg-primary);
    padding: var(--spacing-lg) 0 var(--spacing-md);
    border-top: 2px solid var(--fruitful-gold);
}

.footer-content {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: var(--spacing-md);
    margin-bottom: var(--spacing-md);
}

.footer-section h3,
.footer-section h4 {
    color: var(--fruitful-gold);
    margin-bottom: var(--spacing-sm);
}

.footer-section ul {
    list-style: none;
}

.footer-section ul li {
    margin-bottom: 8px;
}

.footer-section a {
    color: var(--text-secondary);
    text-decoration: none;
    transition: color 0.3s ease;
}

.footer-section a:hover {
    color: var(--fruitful-gold);
}

.social-links {
    display: flex;
    gap: var(--spacing-sm);
    margin-top: var(--spacing-sm);
}

.social-links a {
    width: 40px;
    height: 40px;
    background: var(--bg-card);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.25rem;
    transition: all 0.3s ease;
}

.social-links a:hover {
    background: var(--fruitful-gold);
    color: var(--bg-primary);
    transform: translateY(-3px);
}

.footer-legal {
    border-top: 1px solid var(--bg-card);
    padding-top: var(--spacing-md);
    text-align: center;
}

.treaty-badges {
    display: flex;
    justify-content: center;
    gap: var(--spacing-sm);
    margin-bottom: var(--spacing-sm);
    flex-wrap: wrap;
}

.treaty-badge {
    padding: 6px 12px;
    background: var(--bg-card);
    border-radius: 16px;
    font-size: 0.75rem;
    color: var(--fruitful-gold);
    border: 1px solid var(--fruitful-gold);
}

.copyright {
    color: var(--text-secondary);
    font-size: 0.875rem;
    margin-bottom: 8px;
}

.license-info {
    color: var(--text-secondary);
    font-size: 0.75rem;
}

/* ========================================
   MODAL
   ======================================== */

.modal {
    display: none;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.8);
    z-index: 10000;
    align-items: center;
    justify-content: center;
}

.modal.show {
    display: flex;
}

.modal-content {
    background: var(--bg-card);
    padding: var(--spacing-lg);
    border-radius: 16px;
    max-width: 500px;
    width: 90%;
    position: relative;
}

.modal-close {
    position: absolute;
    top: 20px;
    right: 20px;
    font-size: 2rem;
    cursor: pointer;
    color: var(--text-secondary);
    transition: color 0.3s ease;
}

.modal-close:hover {
    color: var(--fruitful-gold);
}

.modal-content h2 {
    color: var(--fruitful-gold);
    margin-bottom: var(--spacing-sm);
}

.parent-form {
    margin-top: var(--spacing-md);
}

.form-group {
    margin-bottom: var(--spacing-sm);
}

.form-group label {
    display: block;
    margin-bottom: 8px;
    color: var(--text-secondary);
}

.form-group input {
    width: 100%;
    padding: 12px;
    background: var(--bg-primary);
    border: 2px solid var(--bg-secondary);
    border-radius: 8px;
    color: var(--text-primary);
    font-size: 1rem;
}

.form-group input:focus {
    outline: none;
    border-color: var(--fruitful-gold);
}

.btn-block {
    width: 100%;
    margin-top: var(--spacing-sm);
}

/* ========================================
   RESPONSIVE DESIGN
   ======================================== */

@media (max-width: 968px) {
    .hero-content,
    .toys-content {
        grid-template-columns: 1fr;
        text-align: center;
    }

    .hero-title {
        font-size: 2rem;
    }

    .main-nav {
        display: none;
    }

    .cta-buttons {
        justify-content: center;
    }

    .hero-badges {
        justify-content: center;
    }
}

@media (max-width: 640px) {
    .header-content {
        flex-direction: column;
        gap: var(--spacing-sm);
    }

    .section-title {
        font-size: 2rem;
    }

    .big-bear {
        font-size: 6rem;
    }

    .treaty-badges {
        flex-direction: column;
        align-items: center;
    }
}
