        return handleGetShow(showId, env);
      }

      // Learning content API
      if (path === '/api/learning' || path === '/api/learning/') {
        return handleGetLearning(request, env);
      }

      // Parent dashboard API
      if (path === '/api/parent/auth') {
        return handleParentAuth(request, env);
      }

      if (path === '/api/parent/dashboard') {
        return handleParentDashboard(request, env);
