        return new Response(JSON.stringify(result, null, 2), {
          headers: {
            ...corsHeaders,
            'Content-Type': 'application/json',
          },
        });
      }

      // Status endpoint
      if (url.pathname === '/api/status') {
        const status = orchestrator.getStatus();

        return new Response(JSON.stringify(status, null, 2), {
          headers: {
            ...corsHeaders,
            'Content-Type': 'application/json',
          },
        });
      }

      // Focus Node status
      if (url.pathname === '/api/focus-node') {
        const focusNode = orchestrator.codeNest.focusNode;
        const status = {
          atomLevel: focusNode.atomLevel,
          inlineVerification: focusNode.inlineVerification,
          interstellarFocus: focusNode.interstellarFocus,
          banimalLoop: focusNode.banimalLoop,
          philosophy: focusNode.philosophy,
          founder: 'Heyns Schoeman',
