export default {
  async fetch(request, env, ctx) {
    const url = new URL(request.url);
    const payrollOS = new FruitfulPayrollOS();

    // CORS
    const corsHeaders = {
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Methods': 'GET, POST, OPTIONS',
      'Access-Control-Allow-Headers': 'Content-Type',
    };

    if (request.method === 'OPTIONS') {
      return new Response(null, { headers: corsHeaders });
    }

    try {
      // Dashboard
      if (url.pathname === '/' || url.pathname === '/dashboard') {
        const dashboard = payrollOS.getDashboardData();
        return new Response(JSON.stringify(dashboard, null, 2), {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' }
        });
      }

      // Add employee
      if (url.pathname === '/api/employee/add' && request.method === 'POST') {
        const employeeData = await request.json();
        const employee = payrollOS.addEmployee(employeeData);
        return new Response(JSON.stringify(employee, null, 2), {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' }
        });
      }

      // Process payroll
      if (url.pathname === '/api/payroll/process' && request.method === 'POST') {
        const { period } = await request.json();
        const result = await payrollOS.processPayroll(period);
        return new Response(JSON.stringify(result, null, 2), {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' }
        });
      }

      // Audit report
      if (url.pathname === '/api/audit/report') {
        const startDate = new Date(url.searchParams.get('start') || Date.now() - 30 * 24 * 60 * 60 * 1000);
        const endDate = new Date(url.searchParams.get('end') || Date.now());
        const report = payrollOS.auditSystem.generateAuditReport(startDate, endDate);
        return new Response(JSON.stringify(report, null, 2), {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' }
        });
      }

      // Claude Code export
      if (url.pathname === '/api/claude-code') {
        const export Data = payrollOS.exportForClaudeCode();
        return new Response(JSON.stringify(exportData, null, 2), {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' }
        });
      }

      return new Response('Not Found', { status: 404, headers: corsHeaders });

    } catch (error) {
      return new Response(JSON.stringify({ error: error.message }), {
        status: 500,
        headers: { ...corsHeaders, 'Content-Type': 'application/json' }
      });
    }
  },
};

