class CodeNestBuilder {
  constructor(planetMetadata) {
    this.planet = planetMetadata;
  }

  /**
   * Build complete application from user upload
   */
  async buildApplication(upload) {
    console.log('🏗️ Building application with CodeNest...');

    const {
      userFile,
      brandName,
      tagline,
      entrepreneur,
      sector,
      colors,
      content
    } = upload;

    // Get metadata from Planet
    const metadata = await this.planet.pullMetadata(sector);

    // Build application
    const app = {
      brandName: this.sanitizeName(brandName),
      html: this.buildHTML(metadata.templates, { brandName, tagline, content }),
      css: this.buildCSS(metadata.styling, colors),
      js: this.buildJavaScript({ brandName, metadata }),
      metadata: this.buildMetadataCore(upload, metadata),
      subnodes: this.configureSubnodes(metadata.subnodes),
      protocols: metadata.protocols,
      smartToys: metadata.smartToys,
      treaty: metadata.treaties
    };

    console.log('  ✅ Application built successfully');
    return app;
  }

  sanitizeName(name) {
    return name
      .toLowerCase()
      .replace(/[^a-z0-9-]/g, '-')
      .replace(/-+/g, '-')
      .replace(/^-|-$/g, '');
  }

  buildHTML(template, data) {
    return template.html
      .replace('{{BRAND_NAME}}', data.brandName)
      .replace('{{TAGLINE}}', data.tagline)
      .replace('{{CONTENT}}', data.content);
  }

  buildCSS(styling, userColors) {
    return `
      :root {
