      console.log('🌊 Seedwave protocol connecting...');

      // Initialize application
      document.addEventListener('DOMContentLoaded', () => {
        console.log('Application initialized:', BRAND_CONFIG.brandName);
      });
    `;
  }

  buildMetadataCore(upload, planetMetadata) {
    return {
      userId: `USER_${Date.now()}`,
