export default {
  async fetch(request, env, ctx) {
    const url = new URL(request.url);
    const orchestrator = new BaobabMasterOrchestrator();

    // CORS
    const corsHeaders = {
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Methods': 'GET, POST, OPTIONS',
      'Access-Control-Allow-Headers': 'Content-Type',
    };

    if (request.method === 'OPTIONS') {
      return new Response(null, { headers: corsHeaders });
    }

    try {
      // Health check
      if (url.pathname === '/' || url.pathname === '/health') {
        const status = orchestrator.getStatus();
        return new Response(JSON.stringify(status, null, 2), {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' }
        });
      }

      // Activate global lattice
      if (url.pathname === '/api/activate') {
        const result = orchestrator.activate();
        return new Response(JSON.stringify(result, null, 2), {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' }
        });
      }

      // Trunk signal endpoint
      if (url.pathname === '/api/trunk-signal' && request.method === 'POST') {
        const { signal_type, data } = await request.json();
        orchestrator.trunk.processTrunkSignal(signal_type, data);

        return new Response(JSON.stringify({ success: true }), {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' }
        });
      }

      return new Response('Not Found', { status: 404, headers: corsHeaders });

    } catch (error) {
      return new Response(JSON.stringify({ error: error.message }), {
        status: 500,
        headers: { ...corsHeaders, 'Content-Type': 'application/json' }
      });
    }
  },
};

