    }

    try {
      // Health check
      if (url.pathname === '/' || url.pathname === '/health') {
        const status = orchestrator.getStatus();
        return new Response(JSON.stringify(status, null, 2), {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' }
        });
